configuration.tbelDecoder:
/**
 * Decodes the incoming payload and returns a structured object containing telemetry data and attributes.
 *
 * @param {byte[]} input - The raw payload received as an array of bytes.
 * @returns {Object} output - The structured output with decoded telemetry and attributes.
 */

function decodePayload(input) {
    var result = {attributes: {}, telemetry: {}};

    result.telemetry.battery = input[2] / 10.0;
    result.telemetry.humidity = (parseBytesToInt(input, 3, 1) * 256 + parseBytesToInt(input, 4, 1))  / 10.0;

    var temp = parseBytesToInt(input, 5, 1) * 256 + parseBytesToInt(input, 6, 1);
    if (temp >= 0x8000) {
        temp -= 0x10000;
    }
    result.telemetry.temperature = temp / 10.0;

    return result;
}

var result = {};

if (payload != null && payload.length > 0) {
    result = decodePayload(payload);
}

return result;
